{% style %}
.fully-spaced-row--medium {
  margin: 0;
}
.vip-wrapper {
  text-align: center;
  padding: 5vh 16px 40vh;
  background: url("https://cdn.shopify.com/s/files/1/1569/8067/files/VIP_instagram_LP.jpg?v=1731067290");
  background-position: bottom;
  background-size: cover;
  height: 90vw;
  min-height: 100vh;
overflow-y: hidden;
}
.vip-heading {
  font-family: "Bebas Neue";
  letter-spacing: 1px;
  font-size: 2rem;
  margin-bottom: 20px;
}
.vip-text {
  color: white;
  padding-bottom: 15px;
  max-width: 700px;
  margin: auto;
  font-family: "Century Gothic";
}
form p {
  font-family: "Century Gothic", sans-serif !important;
padding: 15px 0;
}
form label {
  font-family: "Bebas Neue" !important;
font-size: 16px !important;
letter-spacing: 1px !important;
font-weight: normal !important;
}
form button {
  max-width: 200px !important;
  margin: auto !important;
}
@media screen and (max-width: 768px) {
  .vip-wrapper {
    padding: 5vh 10% 30vh;
height: 130vh;
  }
  .vip-form {
    margin-top: -25px;
}
}
{% endstyle %}
