{% style %}
.fully-spaced-row--medium {
  margin: 0;
}
.vip-wrapper {
  text-align: center;
  padding: 5vh 16px 40vh;
  background: url("https://cdn.shopify.com/s/files/1/1569/8067/files/WhatsApp_Image_2025-03-03_at_11.17.03_8317cbbd.jpg?v=1741049168");
  background-position: bottom;
  background-size: cover;
  height: 90vw;
  min-height: 100vh;
overflow-y: hidden;
}
.vip-heading {
  font-family: "Bebas Neue";
  letter-spacing: 1px;
  font-size: 2rem;
  margin-bottom: 20px;
color: white;
}
.vip-subheading {
  color: white
}
ul.vip-bullets {
  list-style-type: disc;
  color: white;
margin: 0;
}
ul.vip-bullets li {
  width: fit-content;
margin: auto
}
.vip-text {
  color: white;
  padding-top: 15px;
  max-width: 700px;
  margin: auto;
  font-family: "Century Gothic";
}
form p {
  font-family: "Century Gothic", sans-serif !important;
padding: 15px 0;
}
form label {
  font-family: "Bebas Neue" !important;
font-size: 16px !important;
letter-spacing: 1px !important;
font-weight: normal !important;
}
form button {
  max-width: 200px !important;
  margin: 30px auto 0 !important;
}
@media screen and (max-width: 768px) {
  .vip-wrapper {
    padding: 5vh 10% 30vh;
height: 130vh;
  }
  .vip-form {
    margin-top: -25px;
}
}
{% endstyle %}
